INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `MaleText_lang`, `FemaleText_lang`, `VerifiedBuild`) VALUES
(55317, 'ruRU', 'Отлично, $p. Встретимся в форте Грукин и обсудим наши дальнейшие действия.', '', 21154),
(55320, 'ruRU', 'Я – начало конца... Тень, что заслоняет солнце... Звонящий по вам колокол...', '', 21154),
(55324, 'ruRU', 'Сила Земли, храни их в этот темный час, ибо они последние защитники Азерота!', '', 21154),
(55329, 'ruRU', 'Настало Время Сумерек!', '', 21154),
(55336, 'ruRU', 'Огонь и боль питают мою ненависть!', '', 21154),
(88427, 'ruRU', 'Я готов лететь к башне Эгвин.', 'Я готова лететь к башне Эгвин.', 21154),
(88528, 'ruRU', 'Мы должны наполнить силой тотемы защиты. Иначе Легион прорвет нашу оборону и двинется в Азсуну.', '', 21154),
(88529, 'ruRU', 'Your own order is unraveling. Betrayed from within... a curse you''ll bear for all eternity.', '', 21154),
(88611, 'ruRU', 'ДОЛЖЕН быть способ пробраться мимо этих статуй.$b$bМне очень хочется вернуться в свои покои, но совы не пропускают меня. А я уже так устал...', 'ДОЛЖЕН быть способ пробраться мимо этих статуй.$b$bМне очень хочется вернуться в свои покои, но совы не пропускают меня. А я уже так устала...', 21154),
(88612, 'ruRU', 'Возможно, получится прокрасться мимо них в состоянии |cFF0000FF[невидимости]|r...', 'Возможно, получится прокрасться мимо них в состоянии |cFF0000FF[невидимости]|r...', 21154),
(88613, 'ruRU', 'Я придумаю, как обойти сов.', 'Я придумаю, как обойти сов.', 21154),
(88614, 'ruRU', 'Мне нужна помощь! Как можно незаметно прокрасться мимо сов?', 'Нужна помощь! Как можно незаметно прокрасться мимо сов?', 21154),
(88619, 'ruRU', 'У меня с каждым годом все сильнее урчит в животе. Пожалуй, не помешало бы перекусить.$b$bМожет быть, я даже отдам тебе свой свиток, если ты поможешь мне утолить мой голод.', 'У меня с каждым годом все сильнее урчит в животе. Пожалуй, не помешало бы перекусить.$b$bМожет быть, я даже отдам тебе свой свиток, если ты поможешь мне утолить мой голод.', 21154),
(88620, 'ruRU', 'Хм... Ты ведь практикуешь магию? Наверняка ты сможешь |cFF0000FF[сотворить яства]|r для меня...', 'Хм... Ты ведь практикуешь магию? Наверняка ты сможешь |cFF0000FF[сотворить яства]|r для меня...', 21154),
(88621, 'ruRU', 'Я достану тебе еды.', 'Я достану тебе еды.', 21154),
(88622, 'ruRU', 'Мне нужна помощь! Как накормить голодного спутника?', 'Мне нужна помощь! Как накормить голодного спутника?', 21154),
(88623, 'ruRU', 'Мне пора укладывать дочку спать. Ей уже сто лет назад пора было в кровать.$b$bНо эти стены преграждают мне путь. Что же делать заботливому родителю?', 'Мне пора укладывать дочку спать. Ей уже сто лет назад пора было в кровать.$b$bНо эти стены преграждают мне путь. Что же делать заботливому родителю?', 21154),
(88624, 'ruRU', 'Дети вырастают так быстро. |cFF0000FF[Скачок]|r, другой – смотришь, а они уже и взрослые.', 'Дети вырастают так быстро. |cFF0000FF[Скачок]|r, другой – смотришь, а они уже и взрослые.', 21154),
(88625, 'ruRU', 'Я придумаю, как преодолеть стену.', 'Я придумаю, как преодолеть стену.', 21154),
(88626, 'ruRU', 'Мне нужна помощь! Как можно оказаться по ту сторону этой стены?', 'Мне нужна помощь! Как можно оказаться по ту сторону этой стены?', 21154),
(88627, 'ruRU', 'Мне велели перетравить в лицее всех крыс. Но есть одна очень противная крыса, с которой я решительно ничего не могу сделать.', 'Мне велели перетравить в лицее всех крыс. Но есть одна очень противная крыса, с которой я решительно ничего не могу сделать.', 21154),
(88628, 'ruRU', 'Видно, на нее наложены какие-то очень сильные чары. Конечно, могучий маг мог бы каким-то образом |cFF0000FF[украсть чары]|r, ну так то маг, а я ведь простой уборщик...', 'Видно, на нее наложены какие-то очень сильные чары. Конечно, могучий маг мог бы каким-то образом |cFF0000FF[украсть чары]|r, ну так то маг, а я ведь простая уборщица...', 21154),
(88629, 'ruRU', 'Я разберусь с этой крысой.', 'Я разберусь с этой крысой.', 21154),
(88631, 'ruRU', 'Мне нужна помощь! Как победить это чудовище?', 'Мне нужна помощь! Как победить это чудовище?', 21154),
(88634, 'ruRU', 'Вместе мы закроем гробницу Саргераса и победим Пылающий Легион.', '', 21154),
(88635, 'ruRU', 'Я готов идти на поиски артефакта!', 'Я готова идти на поиски артефакта!', 21154),
(88644, 'ruRU', 'Еда! Какое наслаждение! Спасибо – и можешь взять себе мой свиток заклинаний. Он мне больше не нужен.', 'Еда! Какое наслаждение! Спасибо – и можешь взять себе мой свиток заклинаний. Он мне больше не нужен.', 21154),
(88745, 'ruRU', '<На страницах этой книги записаны различные заклинания; многие из них вам знакомы, но некоторые вы видите впервые. Одно из таких заклинаний немедленно привлекло ваше внимание.>', '', 21154),
(88746, 'ruRU', '<Произнести заклинание призыва Алунета.>', '<Произнести заклинание призыва Алунета.>', 21154),
(88752, 'ruRU', '"Великий посох Алунет пребывает в мире, недосягаемом для всех, за исключением лишь самых искушенных чародеев. Первым из тех, кому удалось призвать его, был верховный маг Рентаррик. Последней была я".$b$b– Магна Эгвин', '"Великий посох Алунет пребывает в мире, недосягаемом для всех, за исключением лишь самых искушенных чародеев. Первым из тех, кому удалось призвать его, был верховный маг Рентаррик. Последней была я".$b$b– Магна Эгвин', 21154),
(88753, 'ruRU', '"Алунет очень своенравен, и он не станет сразу служить тому, кто попытается использовать его как оружие. Он обладает собственной волей, и эту волю сначала нужно сломить".$b$b– Магна Эгвин', '"Алунет очень своенравен, и он не станет сразу служить тому, кто попытается использовать его как оружие. Он обладает собственной волей, и эту волю сначала нужно сломить".$b$b– Магна Эгвин', 21154),
(88754, 'ruRU', '"Алунет очень непостоянен. Призвавший его должен как можно быстрее установить с посохом ментальную связь, иначе он отвлечется и замкнется в себе".$b$b– Магна Эгвин', '"Алунет очень непостоянен. Призвавший его должен как можно быстрее установить с посохом ментальную связь, иначе он отвлечется и замкнется в себе".$b$b– Магна Эгвин', 21154),
(88755, 'ruRU', '"Могущество Алунета сразу очевидно любому его владельцу. Но тонкости обращения с ним становятся ясными лишь со временем".$b$b– Магна Эгвин', '"Могущество Алунета сразу очевидно любому его владельцу. Но тонкости обращения с ним становятся ясными лишь со временем".$b$b– Магна Эгвин', 21154),
(88756, 'ruRU', '"Если владелец Алунета в данный момент не пользуется им, он должен отпустить посох в его родной мир".$b$b– Магна Эгвин', '"Если владелец Алунета в данный момент не пользуется им, он должен отпустить посох в его родной мир".$b$b– Магна Эгвин', 21154),
(88768, 'ruRU', 'А-а-а... Алунет. Великий посох Магны.$b$bЯ с самого начала знал, что это необычное оружие. Моя госпожа просто поднимала руку и доставала его прямо из воздуха... а потом, когда он ей был больше не нужен, точно так же поднимала вверх руку с посохом, и он бесследно исчезал.$b$bИ это были не фокусы и не трюки. Посох на самом деле обитал в ином мире. И Магна знала, как проникнуть в этот мир и достать его оттуда.', 'А-а-а... Алунет. Великий посох Магны.$b$bЯ с самого начала знала, что это необычное оружие. Моя госпожа просто поднимала руку и доставала его прямо из воздуха... а потом, когда он ей был больше не нужен, точно так же поднимала вверх руку с посохом, и он бесследно исчезал.$b$bИ это были не фокусы и не трюки. Посох на самом деле обитал в ином мире. И Магна знала, как проникнуть в этот мир и достать его оттуда.', 21154),
(88769, 'ruRU', 'Как-то раз я видел посох в ее библиотеке. Он парил в воздухе, медленно проплывая мимо стопок книг!$b$bЯ сделал вид, что ничего не заметил, но, мне кажется, посох успел перехватить мой взгляд.', 'Как-то раз я видела посох в ее библиотеке. Он парил в воздухе, медленно проплывая мимо стопок книг!$b$bЯ сделала вид, что ничего не заметила, но, мне кажется, посох успел перехватить мой взгляд.', 21154),
(88770, 'ruRU', 'Ты говоришь об оружии Магны? Я могу рассказать тебе, что мне известно о нем.$b$bДавным-давно, когда я был намного моложе, моя госпожа как-то оставила посох без присмотра – просто прислонила его к стенке у двери в свою комнату. Я попытался прикоснуться к нему, но он больно ударил меня по пальцам сиреневой молнией.$b$bИ в ту же секунду появилась Магна и забрала посох.$b$bУ нее с ним была установлена какая-то особая свя', 'Ты говоришь об оружии Магны? Я могу рассказать тебе, что мне известно о нем.$b$bДавным-давно, когда я была намного моложе, моя госпожа как-то оставила посох без присмотра – просто прислонила его к стенке у двери в свою комнату. Я попыталась прикоснуться к нему, но он больно ударил меня по пальцам сиреневой молнией.$b$bИ в ту же секунду появилась Магна и забрала посох.$b$bУ нее с ним была установлена какая-то особая с', 21154),
(88771, 'ruRU', 'Это оружие... Я никогда раньше не встречал ничего подобного.$b$bМоя госпожа часто разговаривала с посохом, словно он был живой. Они могли часами беседовать о заклинаниях, ритуалах, тонкостях чародейского искусства и прочих материях, недоступных моему пониманию.$b$bМоя госпожа всегда была немного эксцентричной, но я никогда не считал, что у нее не все дома. Хотя, конечно, разговаривать с посохом все же странно.', 'Это оружие... Я никогда раньше не встречала ничего подобного.$b$bМоя госпожа часто разговаривала с посохом, словно он был живой. Они могли часами беседовать о заклинаниях, ритуалах, тонкостях чародейского искусства... все это было недоступно моему пониманию.$b$bМоя госпожа всегда была немного эксцентричной, но я никогда не считала, что у нее не все дома. Хотя, конечно, разговаривать с посохом все же странно.', 21154),
(88772, 'ruRU', 'Что ты можешь рассказать мне об Алунете?', 'Что ты можешь рассказать мне об Алунете?', 21154),
(88931, 'ruRU', '', 'Глокни фумзель. Фумзель дунбук. Дукер слимптень, |3-6($r)!', 21154),
(88932, 'ruRU', '', 'Бортни! БОРТНИ!', 21154),
(88933, 'ruRU', 'Слямтельный фрундель. Гаркнул мой дук! А? Каково? Давай бойцовскую жратву!', 'Слямтельный фрундель. Гаркнул мой дук! А? Каково? Давай бойцовскую жратву!', 21154),
(88934, 'ruRU', '', 'Твой взяла, шнуропс-фрумопс. На жратву. $gЗаслужил:Заслужила;.', 21154),
(88935, 'ruRU', '', 'Шлямзель дукер грокнуть ня? И то – бортельный шнуропс слимптеню не глок! Фумзель!', 21154),
(88936, 'ruRU', 'If we didn''t want the support of these giants, I''d kill you where you stand.', '', 21154),
(88937, 'ruRU', 'No food for you today, slave.', '', 21154),
(88938, 'ruRU', '', 'Говори, демон! Где Легион спрятал тело Иллидана?', 21154),
(90212, 'ruRU', 'Я... ничего... не скажу!', '', 21154),
(90215, 'ruRU', '', 'Ну тогда отведай Косы Душ...', 21154),
(90222, 'ruRU', 'Я отделал твоего братца так, что он в штаны наложил! Давай сюда жратву!', 'Я отделала твоего братца так, что он в штаны наложил! Давай сюда жратву!', 21154),
(90223, 'ruRU', 'Эй, Ука, подбери свой укер. Это негигиенично и вообще отвратительно.', 'Эй, Ука, подбери свой укер. Это негигиенично и вообще отвратительно.', 21154),
(90224, 'ruRU', 'Отвали.', 'Отвали.', 21154),
(90225, 'ruRU', 'Жжется... ЖЖЕТСЯ!', '', 21154),
(90231, 'ruRU', '', 'Иди бортни грязня, шнуропс. А то иди два бортни – один бортельный в глок, два – дукер мрафти заместо.', 21154),
(90232, 'ruRU', '', 'Голова заместо? Дукер грязня заместо голова твоя!', 21154),
(90236, 'ruRU', '', 'Брат не тронь, хлязень грокнутый хрюзендель!', 21154),
(90238, 'ruRU', 'Я хотел бы ознакомиться с твоим ассортиментом крепких напитков.', 'Я хотела бы ознакомиться с твоим ассортиментом крепких напитков.', 21154),
(90247, 'ruRU', '', 'Мрафтать чумс?! Твоя чумс мрафтать! Грокнутый укер шнуропс!', 21154),
(90255, 'ruRU', 'Нам нужно раздобыть оружие, чтобы раздать его остальным заключенным, а тебя выдать за гладиатора от Мрачновода.\n\nПосле этого можно будет заняться и принцем Океанием.', '', 21154),
(90256, 'ruRU', 'Легион поработит всех!', '', 21154),
(90311, 'ruRU', '', 'Да? Что такое? Эта бойцовская жратва – наш семейный секрет.\n\nТак что ничего ты не получишь, |3-6($r).', 21154),
(90885, 'ruRU', '...Фарондис... Приливный камень Голганнета... наша последняя надежда...', '', 21154),
(90886, 'ruRU', 'Это все, что я могу вспомнить. Помоги мне вернуть воспоминания, и тогда я, может быть, сумею помочь тебе.', '', 21154),
(90887, 'ruRU', 'Фарондис, время действовать.', '', 21154),
(90888, 'ruRU', 'Ваша королева Азшара слишком злоупотребляла магией и привлекла внимание целого легиона демонов. У нас осталось очень мало времени.', '', 21154),
(90889, 'ruRU', 'Я согласен... но неужели нет иного выхода, кроме как воспользоваться Приливным камнем? Столь радикальное решение...', '', 21154),
(90890, 'ruRU', 'Хорошо.', '', 21154),
(90891, 'ruRU', 'Я знал, что могу рассчитывать на тебя, Фарондис.', '', 21154),
(90894, 'ruRU', 'Иллидан, мне не нравится план Кенария.', '', 21154),
(90895, 'ruRU', 'Он хочет воспользоваться силой Приливного камня Голганнета! Он намеревается уничтожить сам Источник Вечности!', '', 21154),
(90896, 'ruRU', 'Это же будет конец света!', '', 21154),
(90897, 'ruRU', 'Я знаю. Но тебе не хватит сил помешать Кенарию получить Приливный камень... тут даже я не в силах тебе помочь.', '', 21154),
(90898, 'ruRU', 'Ну и что же нам тогда делать?', '', 21154),
(90899, 'ruRU', 'Только сама королева Азшара может противостоять полубогу.', '', 21154),
(90900, 'ruRU', 'Прости, Кенарий. Я верил в тебя, но не смог заставить себя обратиться против...', '', 21154),
(90901, 'ruRU', '', 'Сегодня ты оказал мне огромную услугу, Фарондис.', 21154),
(90902, 'ruRU', '', 'Жаль только, что тебя уже не будет в живых, чтобы оценить мою благодарность.', 21154),
(90903, 'ruRU', '...что?!', '', 21154),
(90904, 'ruRU', '', 'Меня повсюду окружают заговорщики и предатели.', 21154),
(90905, 'ruRU', '', 'Снюхались с Кенарием за моей спиной?! Я не потерплю инакомыслия!', 21154),
(90908, 'ruRU', 'Смотри. Я призову образы тех немногих воспоминаний, что еще остались у меня... о Приливном камне.', '', 21154),
(90909, 'ruRU', 'Боюсь, что Приливный камень Голганнета – наша последняя надежда.', '', 21154),
(90910, 'ruRU', '', 'Вот он, мой принц: Приливный камень Голганнета, один из семи Столпов Созидания, бесценная реликвия калдорай.', 21154),
(90911, 'ruRU', 'Очень хорошо. Оставь меня.', '', 21154),
(91438, 'ruRU', '', 'Who goes there? Keep your hands and weapons where I can see them.', 21154),
(91439, 'ruRU', '', 'Prove to me that you are not an enemy.', 21154),
(91447, 'ruRU', 'Прости меня, отец.', '', 21154),
(91448, 'ruRU', 'Я смотрю, больно ты любишь рот разевать, Ука. Сейчас вот набью тебе полную пасть жратвой – и сиди жуй.', 'Я смотрю, больно ты любишь рот разевать, Ука. Сейчас вот набью тебе полную пасть жратвой – и сиди жуй.', 21154),
(91449, 'ruRU', 'Ah, visitors. It''s been a long time since Mardum, Allari.', '', 21154),
(91450, 'ruRU', '', '', 21154),
(91503, 'ruRU', '', 'Your orders are clear! Find the Tidestone, and return it to Her Glory, Queen Azshara!', 21154),
(91504, 'ruRU', '', 'These creatures only pretend to understand its power. The Tidestone belongs to Azshara, Her Radiance, the Light Beneath the Tides!', 21154),
(91505, 'ruRU', '', 'Scour the sands!', 21154),
(91506, 'ruRU', '', 'Pillage the ruins!', 21154),
(91507, 'ruRU', '', 'Slay anyone and anything that gets in your path!', 21154),
(91508, 'ruRU', '', 'FIND THAT TIDESTONE!', 21154),
(91509, 'ruRU', '', 'Your Queen Azshara wills it, and so shall it be done!', 21154),
(91555, 'ruRU', 'Ты $Gхотел:хотела; говорить со мной?', '', 21154),
(91558, 'ruRU', 'Твои собратья обращаются с тобой так неуважительно... Почему? За что?', 'Твои собратья обращаются с тобой так неуважительно... Почему? За что?', 21154),
(91583, 'ruRU', 'Это все – дело твоих рук? Но как? Зачем?', 'Это все – дело твоих рук? Но как? Зачем?', 21154),
(91584, 'ruRU', '', 'Дукер в укер, склимптень хмырень-шнуропс!', 21154),
(91585, 'ruRU', '', 'Горканутый дукер-нюхер! Грючь вонючие словяки взад!', 21154),
(91587, 'ruRU', 'You want to fight in the arena? No? Work harder!', '', 21154),
(91591, 'ruRU', 'Грязнеедка! Да-да, я так и сказал! Грязнеедка!', 'Грязнеедка! Да-да, я так и сказала! Грязнеедка!', 21154),
(91592, 'ruRU', 'I am taking my leave, nightwatchers... though I doubt you will miss me.', '', 21154),
(91593, 'ruRU', 'Hmm. How long has this road been flooded? I must have the groundskeepers attend to it.', '', 21154),
(91594, 'ruRU', 'Look out!', '', 21154),
(91595, 'ruRU', 'Gah! Watch out!', '', 21154),
(91596, 'ruRU', 'Thaldrys, my trusted captain.', '', 21154),
(91597, 'ruRU', 'Слышь, Ука! У тебя дук вместо головы!', 'Слышь, Ука! У тебя дук вместо головы!', 21154),
(91598, 'ruRU', 'If the giants didn''t want you alive for their sport, you''d be dead.', '', 21154),
(91599, 'ruRU', 'Ну что, пошли искать Приливный камень.', 'Ну что, пошли искать Приливный камень.', 21154),
(91600, 'ruRU', 'Вы освободили ЕГО?! Он же был врагом Иллидана!', '', 21154),
(91607, 'ruRU', 'Ты пока поведешь иллидари в бой против демонов, а я прикину, каким будет наш следующий ход здесь на Азсуне.\n\nМы должны выяснить, где находится этот Столп созидания. Я точно знаю, что он где-то тут.', '', 21154),
(91608, 'ruRU', 'Пойдем, Кадгар.', 'Пойдем, Кадгар.', 21154),
(91610, 'ruRU', 'I remember - I stowed the Tidestone within the confines of the Nar''thalas Academy.', '', 21154),
(91611, 'ruRU', 'That was a very long time ago.', '', 21154),
(91612, 'ruRU', 'Come with me.', '', 21154),
(91613, 'ruRU', 'The elves in this area are much less hospitable than I am. Tread carefully.', '', 21154),
(91614, 'ruRU', 'Фарондис! Предатель! Ты обрек нас на все это!', '', 21154),
(91615, 'ruRU', '', 'УМРИ... ФАРОНДИС...', 21154),
(91616, 'ruRU', 'Here, the Nar''thalas Academy. This is where it all happened.', '', 21154),
(91617, 'ruRU', 'Пожалуйста, покажи мне, где лежит Приливный камень.', 'Пожалуйста, покажи мне, где лежит Приливный камень.', 21154),
(91620, 'ruRU', 'Пам-парам-хоп-хоп-хоп! А ну за работу – не то сразу в гроб!', '', 21154),
(91621, 'ruRU', 'Китопуз может серьезно осложнить нам задачу. Постарайся его не раздражать.', '', 21154),
(91622, 'ruRU', 'И что здесь делает $gэтот жалкий:эта жалкая:r; |3-6($r)?\n\nНичего, тут тебя быстро превратят в отбивную.', '', 21154),
(91623, 'ruRU', 'Босс Китопуз... Какая приятная встреча. Просто хотел тебе сказать, что к вечеру нынешнего дня я стану чемпионом арены.', 'Босс Китопуз... Какая приятная встреча. Просто хотела тебе сказать, что к вечеру нынешнего дня я стану чемпионкой арены.', 21154),
(91627, 'ruRU', 'Я тебе и твоему дружку спуску не дам. Думаешь, сможешь выстоять? Думаешь, справишься с тем, что тебя ждет?\n\nДа как бы не так.\n\nК концу дня будешь $gполумертвый:полумертвая; валяться на арене, моля о пощаде. А я с радостью пну тебя сапогом по роже, |3-6($r).', '', 21154),
(91628, 'ruRU', 'Вот тут ты ошибаешься. Очень сильно ошибаешься.', '', 21154),
(91629, 'ruRU', 'Видишь этот клинок? Я его по самую рукоять засуну тебе в...', '', 21154),
(91630, 'ruRU', 'Погоди... что?', '', 21154),
(91631, 'ruRU', 'Я тебя достану, $p, пусть это и будет последнее, что я сделаю.', '', 21154),
(91632, 'ruRU', 'Китопуз, что все это значит?', '', 21154),
(91633, 'ruRU', 'Тебя предупреждали. С гладиаторами противника – никаких разговоров!', '', 21154),
(91634, 'ruRU', 'Хватит языком трепать. Скоро твоя схватка.', '', 21154),
(91638, 'ruRU', 'Либо побеждай, либо даже не вздумай ко мне на коленях назад ползти.', '', 21154),
(91649, 'ruRU', 'Твоя смелость мне нравится, $gмалыш:малышка;. Я с радостью выпущу тебе кишки на глазах у всего моего батальона.', '', 21154),
(91650, 'ruRU', 'Госпожа Атисса! Я... подвел тебя...', '', 21154),
(91651, 'ruRU', '', 'Да что здесь вообще происходит?', 21154),
(91652, 'ruRU', '', 'Мирмидоны! Убейте незваных гостей!', 21154),
(91653, 'ruRU', 'С удовольствием, моя госпож-жа-а...', '', 21154),
(91654, 'ruRU', 'Выходи на арену – и убивай. Все остальное не важно.', '', 21154),
(91655, 'ruRU', 'Бейся так, будто от этого зависит твоя жизнь. Это, кстати, так и есть.', '', 21154),
(91656, 'ruRU', 'Что, у меня кроме тебя никого и не осталось?', '', 21154),
(91657, 'ruRU', 'Делай, что хочешь, главное – победа. Используй грязные приемы, песок противнику в глаза бросай – мне все равно. Только победа!', '', 21154),
(91658, 'ruRU', 'Надо потопить еще один корабль да набрать себе новых бойцов, поприличнее.', '', 21154),
(91659, 'ruRU', 'Проткни печенку! Вырви селезенку!', '', 21154),
(91660, 'ruRU', 'Я хочу, чтобы весь песок пропитался кровью – и желательно не твоей!', '', 21154),
(91661, 'ruRU', 'Я выбираю тебя.', '', 21154),
(91662, 'ruRU', 'Я верю в тебя, $gмалыш:малышка;.', '', 21154),
(91663, 'ruRU', 'Просто покажи все, что ты можешь.', '', 21154),
(91664, 'ruRU', 'Победишь ты или проиграешь – для меня все равно ты будешь $gлучшим:лучшей;.', '', 21154),
(91665, 'ruRU', 'Не надо бояться. У тебя все получится.', '', 21154),
(91666, 'ruRU', 'Не слушай старину Рулевода. Он такой злой, потому что его никто не любит.', '', 21154),
(91667, 'ruRU', 'У тебя все получится – главное верить в себя.', '', 21154),
(91668, 'ruRU', 'Иди, дружище. Сражайся так, чтобы я мог гордиться тобой.', '', 21154),
(91669, 'ruRU', 'Я вам всем глотки перережу, если не дождусь от вас побед!', '', 21154),
(91670, 'ruRU', 'Вы все бесполезнее плавучего мусора!', '', 21154),
(91671, 'ruRU', 'Твоя зверушка жульничает, Мрачновод. Ты за это заплатишь.', '', 21154),
(91672, 'ruRU', 'Я сейчас сотру эту наглую ухмылку с твоей хари.', '', 21154),
(91673, 'ruRU', 'Избиение младенцев – приятное занятие. Я еще не выставил своих лучших бойцов.', '', 21154),
(91674, 'ruRU', 'Упал – так и лежи. Я тебя еще не так бы отделал.', '', 21154),
(91675, 'ruRU', 'Вы все – жалкие ничтожества. Слышите? Ничтожества!', '', 21154),
(91676, 'ruRU', 'Теперь-то любому очевидно, как жалки и слабы малые расы. Нужно истребить их все.', '', 21154),
(91677, 'ruRU', 'Хнычь, хнычь, крабик. Так тебе и надо!', '', 21154),
(91678, 'ruRU', 'Ха! Разумеется, я победил. А что, в этом были какие-то сомнения?', '', 21154),
(91679, 'ruRU', 'Признай уже, Мрачновод. У твоих жалких слабаков просто нет воли к победе.', '', 21154),
(91680, 'ruRU', 'А ты будешь сражаться, пока тебя не убьют или у него не останется питомцев.', '', 21154),
(91681, 'ruRU', 'В следующий раз сражайся лучше, или я тебя раздавлю, как букашку.', '', 21154),
(91682, 'ruRU', 'И еще одно очко в мою пользу! Ты жалкий слабак, Мрачновод! Ты покойник!', '', 21154),
(91683, 'ruRU', 'Вот так вот и одерживаются победы! А вы смотрите, да мотайте на ус!', '', 21154),
(91684, 'ruRU', 'Запомните и выучите наизусть. Победа любой ценой. ПОБЕДА-ЛЮБОЙ-ЦЕНОЙ!', '', 21154),
(91685, 'ruRU', 'НЕ-Е-ЕТ!!!', '', 21154),
(91686, 'ruRU', 'О нет, мой маленький друг...', '', 21154),
(91687, 'ruRU', 'Покойся с миром, друг. Ты $gзаслужил:заслужила; это.', '', 21154),
(91688, 'ruRU', 'Рулевод, ты негодяй. Твой боец жульничал!', '', 21154),
(91689, 'ruRU', 'Ты $gсражался:сражалась; доблестно и достойно.', '', 21154),
(91690, 'ruRU', 'Не волнуйтесь, друзья мои. В следующий раз мы непременно победим.', '', 21154),
(91691, 'ruRU', 'Поднимем кружки за нашего павшего товарища.', '', 21154),
(91692, 'ruRU', 'Веселей, ребята! У Рулевода бойцы поопытней, но у нас крепче дух!', '', 21154),
(91693, 'ruRU', 'Хорошие парни победили!', '', 21154),
(91694, 'ruRU', 'ДА! Ты прекрасно $gсражался:сражалась;.', '', 21154),
(91695, 'ruRU', 'Тебя просто не остановить, $gмалыш:малышка;.', '', 21154),
(91696, 'ruRU', 'Вот теперь пошло веселье!', '', 21154),
(91697, 'ruRU', 'Зрелище было отличное. Может, в следующий раз оставим противнику жизнь?', '', 21154),
(91698, 'ruRU', 'Твое здоровье, Рулевод!', '', 21154),
(91699, 'ruRU', 'Команда Мрачновода снова побеждает! Ура!', '', 21154),
(91700, 'ruRU', 'Отличное выступление, команда. Теперь главное – не растерять преимущество.', '', 21154),
(91711, 'ruRU', 'Good luck on your quest for the Tidestone, $n.', '', 21154),
(91712, 'ruRU', 'I dare not enter that academy, but I swear I will aid you in whatever way I can.', '', 21154),
(91713, 'ruRU', '', 'Как ты смеешь обнажать оружие в этих священных залах?!', 21154),
(91714, 'ruRU', '', 'Пора преподать тебе урок!', 21154),
(91715, 'ruRU', '', 'А, тебе нужен Приливный камень, не так ли?', 21154),
(91716, 'ruRU', '', 'Не-е-ет! Я должна... защитить... Приливный камень...', 21154),
(91717, 'ruRU', 'От тебя разит смрадом этого предателя Фарондиса...', 'От тебя разит смрадом этого предателя Фарондиса...', 21154),
(91718, 'ruRU', 'Бестолочь! Ты отрываешь меня от занятий!', 'Бестолочь! Ты отрываешь меня от занятий!', 21154),
(91719, 'ruRU', 'Сал''дрен-иль-натас!', 'Сал''дрен-иль-натас!', 21154),
(91720, 'ruRU', 'К нам проникли чужаки! Скорее доложите директрисе!', 'К нам проникли чужаки! Скорее доложите директрисе!', 21154),
(91721, 'ruRU', 'УБИРАЙСЯ... ПРОЧЬ...', '', 21154),
(91722, 'ruRU', 'ГДЕ ФАРОНДИС?..', '', 21154),
(91723, 'ruRU', 'УБЕЙТЕ ЧУЖАКОВ...', '', 21154),
(91724, 'ruRU', 'Я ДОЛЖЕН ЗАЩИТИТЬ ПРИЛИВНЫЙ КАМЕНЬ...', '', 21154),
(91725, 'ruRU', '', 'How perfectly convenient... the brainless $r led us right to the Tidestone.', 21154),
(91726, 'ruRU', '', 'Our Queen Beneath the Tides can reassemble the pieces.', 21154),
(91727, 'ruRU', '', 'As for you - you will make an acceptable slave.', 21154),
(91730, 'ruRU', 'Эй, ты! И куда это ты направляешься?', '', 21154),
(91731, 'ruRU', 'Тебе сюда нельзя.', '', 21154),
(91732, 'ruRU', 'Будь $gосторожен:осторожна;, $n. У Дхоу очень чуткий сон.', '', 21154),
(91733, 'ruRU', 'Пока ты будешь заниматься своим делом, я отправлюсь в Бухту Океания.', '', 21154),
(91734, 'ruRU', 'Я уже свободен – и я вооружу тамошних гладиаторов. С их помощью у нас будет больше шансов против великанов.', '', 21154),
(91743, 'ruRU', 'Если я еще раз тебя здесь увижу...', '', 21154),
(91745, 'ruRU', 'Вообще-то я тут как бы сплю!', '', 21154),
(91747, 'ruRU', 'Думаешь, сможешь проскользнуть мимо меня?', '', 21154),
(91750, 'ruRU', 'Это не для тебя!', '', 21154),
(91751, 'ruRU', 'В следующий раз я тебя в лепешку расплющу!', '', 21154),
(91755, 'ruRU', 'Еще раз увижу твою рожу...', '', 21154),
(91768, 'ruRU', '$p! Ха! Ты $gпоследний:последняя; из гладиаторов Мрачновода.', '', 21154),
(91769, 'ruRU', 'Иди сюда! Давай уже покончим с этим!', '', 21154),
(91770, 'ruRU', 'Давай, Фергюс. Твой выход. Убей ради меня.', '', 21154),
(91771, 'ruRU', 'Ну что, пора умирать, |3-6($pr)?', '', 21154),
(91772, 'ruRU', 'Удачи тебе, $gмалыш:малышка;.', '', 21154),
(91773, 'ruRU', 'Отличный бой, $p! Осталось всего два бойца.', '', 21154),
(91775, 'ruRU', 'Не подведи меня, рыбочеловек, не то насажу тебя на крючок вместо наживки.', '', 21154),
(91776, 'ruRU', 'Да пребудет с тобой благословение Нефритовой Змеи!', '', 21154),
(91777, 'ruRU', 'Ты все равно проиграешь.', '', 21154),
(91778, 'ruRU', 'Да!', '', 21154),
(91779, 'ruRU', 'НЕ-Е-Е-Т!', '', 21154),
(91780, 'ruRU', 'Покончи с этим, Китопуз!', '', 21154),
(91781, 'ruRU', 'Ну же, Рулевод. Пора уже и меня выпускать!', '', 21154),
(91782, 'ruRU', 'Я же сказал, что покромсаю тебя в мелкие кусочки, $p.', '', 21154),
(91783, 'ruRU', 'Именно это я и собираюсь сделать!', '', 21154),
(91784, 'ruRU', 'Чертов клыкарр!', '', 21154),
(91785, 'ruRU', 'Ладно, Мрачновод, в этот раз победа за тобой. Но больше это не повторится.', '', 21154),
(91786, 'ruRU', 'Невероятное представление, $p. А теперь иди, найди своего друга.', '', 21154),
(91787, 'ruRU', 'ЧТО?! Нет...', '', 21154),
(91790, 'ruRU', 'Ну что, $gготов:готова; к выходу на арену?', '', 21154),
(91791, 'ruRU', 'Я всю жизнь мечтал победить Рулевода в настоящей схватке с тремя его бойцами. Они действительно одни из лучших!\n\nМожет быть, именно ты поможешь моей мечте осуществиться, $n?', '', 21154),
(91792, 'ruRU', '$n! Я так рад тебя видеть!\n\nЕсли захочешь, ты в любое время можешь вернуться, чтобы сразиться за меня.', '', 21154),
(91793, 'ruRU', 'Они готовы, Мрачновод. Бросай меня в бой.', 'Они готовы, Мрачновод. Бросай меня в бой.', 21154),
(91794, 'ruRU', 'Надеюсь, команда Мрачновода сможет гордиться тобой.', '', 21154),
(91796, 'ruRU', 'Скремп! Дукер тебе глотку!', '', 21154),
(91797, 'ruRU', 'Я глокать бортельно отсюда.', '', 21154),
(91798, 'ruRU', 'Ука что за слямптень?! Грючный бой низя!', '', 21154),
(91799, 'ruRU', 'Бортельный шнуропс! Сбежал – прям как твой братец!', 'Бортельный шнуропс! Сбежал – прям как твой братец!', 21154),
(91800, 'ruRU', '', 'Parjesh! Rally the rest of our forces. I shall make short work of this minor interruption.', 21154),
(91801, 'ruRU', 'Слушаюсь, повелительница.', '', 21154),
(91808, 'ruRU', 'Что случилось, Фарондис?', 'Что случилось, Фарондис?', 21154),
(91809, 'ruRU', 'Guilt overcame me. I could not simply WAIT while you fought my battles.', '', 21154),
(91810, 'ruRU', 'I resolved to follow you into the academy.', '', 21154),
(91811, 'ruRU', '', 'Make sure that prisoner doesn''t awaken, Parjesh.', 21154),
(91812, 'ruRU', 'Да, госпожа.', '', 21154),
(91813, 'ruRU', 'I followed them as closely as I dared.', '', 21154),
(91814, 'ruRU', 'In the midst of the chaos, I saw it: the sorceress had dropped the Tidestone.', '', 21154),
(91815, 'ruRU', 'Разберись с нашим гостем, Атисса, а я доставлю добычу в узилище.', '', 21154),
(91816, 'ruRU', '', 'Curse you and your kind, elf. The queen herself will hear of this!', 21154),
(91817, 'ruRU', 'Tidestone shards in hand, I set off to rescue you, my champion.', '', 21154),
(91818, 'ruRU', 'At the entrance to the cave, however, I ran into an old acquaintance.', '', 21154),
(91819, 'ruRU', 'I had finally found their slave pen.', '', 21154),
(91820, 'ruRU', '', 'Be gone, spirit! The Tidestone belongs to us now!', 21154),
(91821, 'ruRU', 'I believe you know the rest of the story, $n.', '', 21154),
(91832, 'ruRU', '<Рюкзак Окуны Большого Бивня лежит на земле. Одна его лямка порвана; вокруг видны следы борьбы. Впрочем, все содержимое рюкзака цело.>\n\n<Вероятнее всего, Окуну схватили и отволокли к принцу Океанию. Что ж, дальше придется идти без него.>', '', 21154),
(91833, 'ruRU', 'Свобода!', 'Свобода!', 21154),
(91834, 'ruRU', 'А-а-а! Сейчас ты умрешь, гнусное создание.', 'А-а-а! Сейчас ты умрешь, гнусное создание.', 21154),
(91835, 'ruRU', 'Это не мое оружие – но в руке лежит как влитое.', 'Это не мое оружие – но в руке лежит как влитое.', 21154),
(91836, 'ruRU', 'Пора заплатить цену стали, скроги.', 'Пора заплатить цену стали, скроги.', 21154),
(91837, 'ruRU', 'Восстаньте. Убьем их всех до одного.', 'Восстаньте. Убьем их всех до одного.', 21154),
(91838, 'ruRU', 'Лучше умереть свободным, чем жить в рабстве.', 'Лучше умереть свободной, чем жить в рабстве.', 21154),
(91839, 'ruRU', 'Смерть великанам! Смерть принцу Океанию!', 'Смерть великанам! Смерть принцу Океанию!', 21154),
(91840, 'ruRU', 'Убейте их всех. Нептулон потом заберет своих.', 'Убейте их всех. Нептулон потом заберет своих.', 21154),
(91843, 'ruRU', '$gМой герой:Моя героиня;! Скажи, у тебя случаем оружия какого лишнего не завалялось?', '', 21154),
(91844, 'ruRU', 'Превосходно! Теперь можно приниматься за дело.', '', 21154),
(91845, 'ruRU', 'Ты начинай, а я постараюсь держаться в тени.', '', 21154),
(91846, 'ruRU', 'Чем быстрее мы разберемся с Океанием, тем скорее выберемся отсюда.', '', 21154),
(91847, 'ruRU', 'Невероятно! Мы справились! С Океанием покончено!\n\nТеперь мы свободны.', '', 21154),
(91848, 'ruRU', 'Эй, $p, я здесь, в клетке. Выпусти меня!', '', 21154),
(91849, 'ruRU', 'Тебе конец, Океаний!', '', 21154),
(91852, 'ruRU', 'Вы хотели аудиенции? Сейчас я ее вам организую!', '', 21154),
(91859, 'ruRU', 'ДА! Увидимся внизу.', '', 21154),
(91890, 'ruRU', 'Спасибо тебе, $gчужестранец:чужестранка;.', '', 21154),
(91891, 'ruRU', 'Я даже не знаю, что произошло. Я услышал грохот падения чего-то тяжелого и... оказался здесь в этом стесненном положении. Вот с тех пор сижу тут и жду.', '', 21154),
(91892, 'ruRU', 'Интересно, куда убежала та красивая девчонка с бирюзовыми бусиками?', '', 21154),
(91913, 'ruRU', '', 'Пылающий Легион захватил Казематы, большинство моих Стражей убиты. Поэтому я отпускаю тебя: никто не умеет убивать демонов лучше, чем ты, $n.\n\nЗахватчики направляются к гробнице Иллидана. Мы должны действовать заодно, если хотим остановить Легион.', 21154),
(91914, 'ruRU', 'Полагаю, нам придется пойти на компромисс с нашими тюремщиками... на какое-то время.', '', 21154),
(91915, 'ruRU', 'Что толку торчать в этой тюрьме? Мы должны выбраться отсюда как можно скорее.', '', 21154),
(91916, 'ruRU', 'Это было давно, Кайн. Я все еще верю в наше дело. Но уже никому слепо не подчиняюсь... даже Иллидану.', '', 21154),
(91940, 'ruRU', '<Храпит>', '', 21154),
(91975, 'ruRU', 'Ransacking', 'Ransacking', 21154),
(91982, 'ruRU', '', 'НЕТ! Это не для тебя! Это для принца Океания!', 21154),
(91983, 'ruRU', '', 'Проснись! Я желаю видеть принца Океания! Немедленно!', 21154),
(91984, 'ruRU', '', 'Вот же ты сонный ленивый увалень. Я не стану здесь дожидаться весь день. Я требую аудиенции у принца!', 21154),
(91987, 'ruRU', '', 'Если твоему принцу не нужны эти сокровища, я их заберу и уйду.', 21154),
(91991, 'ruRU', '', 'Если твоего принца не интересуют сокровища, может, ему нужна другая мотивация?', 21154),
(92014, 'ruRU', 'Убирайся с моих глаз, мелюзга, или я тобой закушу и выплюну.', '', 21154),
(92015, 'ruRU', '$n, сюда! Скорее! Помоги мне выбраться из этой клетки.', '', 21154),
(92145, 'ruRU', 'Searching for Clues', 'Searching for Clues', 21154),
(92146, 'ruRU', 'Мы направлялись в... Штормхейм.', 'Мы направлялись в... Штормхейм.', 21154),
(92147, 'ruRU', 'Корабль потерпел крушение... на нас напали жаблины.', 'Корабль потерпел крушение... на нас напали жаблины.', 21154),
(92148, 'ruRU', 'Королева макрур... демон...', 'Королева макрур... демон...', 21154),
(92149, 'ruRU', 'Шторм... нас выбросило на берег.', 'Шторм... нас выбросило на берег.', 21154),
(92150, 'ruRU', 'Капитана... схватили...', 'Капитана... схватили...', 21154),
(92151, 'ruRU', 'Мы... шли в Штормхейм... от Расколотого берега.', 'Мы... шли в Штормхейм... от Расколотого берега.', 21154),
(92152, 'ruRU', 'Демон... здесь...', 'Демон... здесь...', 21154),
(92153, 'ruRU', 'Говорят... макруры съели... капитана!', 'Говорят... макруры съели... капитана!', 21154),
(92154, 'ruRU', 'Королева...', '', 21154),
(92155, 'ruRU', 'Ах, |3-6($r), как замечательно. Подойди поближе ко мне, дитя мое.', '', 21154),
(92156, 'ruRU', 'О чем я только думал? Это твоя битва, моя королева.', '', 21154),
(92157, 'ruRU', 'Бесполезное создание – как и весь ее род. Но ничего – я сам разберусь с вами!', '', 21154),
(92158, 'ruRU', 'ЭЙ! Это не для тебя.', '', 21154),
(92159, 'ruRU', 'Не следует считать нас контрабандистами. Скорее, мы профессиональные спасатели.\n\nМы эксперты в области утилизации. Наша специальность – плавучий мусор, выброшенные и затонувшие грузы, остовы погибших кораблей – и, конечно же, предметы, представляющие интерес для археологов.', '', 21154),
(92160, 'ruRU', '%s манит матроса к себе.', '', 21154),
(92161, 'ruRU', '$gНезнакомец:Незнакомка;, сюда. Скорее.', '', 21154),
(92162, 'ruRU', 'Я жду ваших указаний, $gмой господин:моя госпожа;.', '', 21154),
(92163, 'ruRU', 'Ты, Кор''вас и Циана нанесете удар с тыла. А потом мы зачистим остров.', '', 21154),
(92164, 'ruRU', '', 'Вам всем следует преподать хороший урок.', 21154),
(92190, 'ruRU', 'Пылающий Легион каким-то образом нейтрализует мои тотемы защиты от демонов. Если мы не вернем им силу, скоро наша оборона падет, и все мои старания пойдут прахом.', '', 21154),
(92192, 'ruRU', 'Добро пожаловать в нашу скромную контору, $gпутешественник:путешественница;.', '', 21154),
(92193, 'ruRU', 'I saw my chance. I would attack the naga leaders and save you from captivity.', '', 21154),
(92195, 'ruRU', '"Прогуляться" он пошел... Как же! Наверняка опять отправился на свидание с этой простолюдинкой, в которой он души не чает.', '', 21154),
(92196, 'ruRU', '', 'Алдрит! Ты же не на свидание с этой служанкой ходил?', 21154),
(92197, 'ruRU', 'Что? Нет-нет, конечно, нет...', '', 21154),
(92198, 'ruRU', '...', '', 21154),
(92199, 'ruRU', 'Я встречался с другой девушкой.', '', 21154),
(92200, 'ruRU', 'Ну и что мы будем делать?', '', 21154),
(92201, 'ruRU', 'Запытаем? Поставим на тяжелые работы? Убьем?', '', 21154),
(92202, 'ruRU', 'Думаю, лучше всего убить.', '', 21154),
(92203, 'ruRU', 'Отличная мысль. Убьем так убьем.', '', 21154),
(92204, 'ruRU', 'А?', '', 21154),
(92205, 'ruRU', 'What?', '', 21154),
(92206, 'ruRU', 'Десять тысяч лет назад я невольно связался с одним повстанческим альянсом.', '', 21154),
(92207, 'ruRU', 'Это было во времена расцвета владычества королевы Азшары над эльфами. Ее злоупотребление магией перешло все разумные границы.', '', 21154),
(92208, 'ruRU', 'И мы планировали использовать против нее Приливный камень Голганнета.', '', 21154),
(92209, 'ruRU', 'Кенарий, я умею обращаться с Приливным камнем, но должен тебя предупредить – мы рискуем уничтожить сам Источник Вечности!', '', 21154),
(92210, 'ruRU', 'А вот в этом, друг мой, как раз и заключается план.', '', 21154),
(92211, 'ruRU', 'Пфф! Нам нужно всего лишь разобраться с королевой Азшарой, а не уничтожать сам источник силы всего нашего народа.', '', 21154),
(92212, 'ruRU', 'Согласен. Мы палим из пушки по воробьям.', '', 21154),
(92213, 'ruRU', '', '', 21154),
(92214, 'ruRU', '', 'В эту самую минуту королева Азшара заключает нечестивые пакты с демонами. Она подвергает риску само существование нашего мира.', 21154),
(92215, 'ruRU', 'Хорошо. Принц Фарондис, ты сможешь достать Приливный камень Голганнета?', '', 21154),
(92216, 'ruRU', 'Да, я это устрою, Кенарий.', '', 21154),
(92217, 'ruRU', 'You there. Stop!', '', 21154),
(92230, 'ruRU', '', 'Вместе мы выпустим из самоцветов души демонов и с помощью Косы заставим их говорить.\n\nДай мне знать, когда будешь $gготов:готова; начинать.', 21154),
(92233, 'ruRU', 'Я ничего тебе не скажу, Пожирательница Душ!', '', 21154),
(92234, 'ruRU', '', 'После битвы на Расколотом берегу само собой так вышло, что Кирин-Тором стал править не я, а Кадгар.', 21154),
(92235, 'ruRU', 'Пришло время забыть о наших разногласиях. Вместе Даларан сокрушит армии Пылающего Легиона!', '', 21154),
(92236, 'ruRU', 'Ука, не поделишься со мной своей вкуснейшей бойцовской жратвой? Говорят, это лучшая еда, которую только можно достать!', 'Ука, не поделишься со мной своей вкуснейшей бойцовской жратвой? Говорят, это лучшая еда, которую только можно достать!', 21154),
(92237, 'ruRU', '', 'Где Легион спрятал тело Иллидана? Говори – или обречешь себя на неописуемые страдания.', 21154),
(92238, 'ruRU', 'I''m ready, Allari. Let''s begin.', 'I''m ready, Allari. Let''s begin.', 21154),
(92239, 'ruRU', '', 'Что случилось? Что вам предлагали демоны?', 21154),
(92240, 'ruRU', '', 'Кто с тобой это сделал?', 21154),
(92241, 'ruRU', '', 'Им удалось кого-нибудь переманить?', 21154),
(92242, 'ruRU', '', 'Вместе мы нанесем Легиону удар в спину.', 21154),
(92243, 'ruRU', '', 'Их пытали.', 21154),
(92244, 'ruRU', '', 'Кажется, я понимаю, почему ничего не выходит.', 21154),
(92245, 'ruRU', '', '$p, тебе нужно будет $gсамому:самой; войти в самоцвет души и заставить Арев''наала раскрыть тебе местонахождение Иллидана.', 21154),
(92246, 'ruRU', '', 'Могущественный демон, заточенный в этих самоцветах, помогает им сопротивляться.', 21154),
(92247, 'ruRU', 'Наша воля крепче твоей косы. Я ничего тебе не скажу.', '', 21154),
(92248, 'ruRU', '', 'Иллидари, что здесь произошло?', 21154),
(92252, 'ruRU', '', 'Скорее, $p, – расколи следующий самоцвет души.', 21154),
(92337, 'ruRU', '', 'Быстрее, $p. Нужно найти ключ от демонических замков. Бьюсь об заклад, он у того тюремщика на востоке.', 21154),
(92339, 'ruRU', '', 'Забирай ключ. Нужно освободить драконицу.', 21154),
(92340, 'ruRU', 'Who are you? You''re no slave!', '', 21154),
(92341, 'ruRU', 'Where are you going?', '', 21154),
(92342, 'ruRU', 'What''s the meaning of this?', '', 21154),
(92343, 'ruRU', 'Пылающий Легион захватил Фаронаар. Но у меня есть план – я знаю, как сдержать и разгромить их, пока они целиком не захватили Азсуну.', '', 21154),
(92344, 'ruRU', 'Пылающий Легион движется по Расколотым островам, словно лесной пожар.\n\nМы должны выследить его отряды и уничтожать их, где бы они ни появились.\n\nА твоя задача – закрыть гробницу Саргераса.', '', 21154),
(92345, 'ruRU', 'Пойдем же и вместе поприветствуем Сенегоса.', '', 21154),
(92346, 'ruRU', 'Что-то не так. Похоже, ему плохо.', '', 21154),
(92347, 'ruRU', 'Верховный маг Кадгар? И ты с ним? Вот так сюрприз.', '', 21154),
(92358, 'ruRU', 'Приливный камень зовет их...', 'Приливный камень зовет их...', 21154),
(92360, 'ruRU', 'Моя королева.', '', 21154),
(92391, 'ruRU', 'Я буду сражаться на твоей стороне.', 'Я буду сражаться на твоей стороне.', 21154),
(92392, 'ruRU', 'Надеюсь, с них этого было довольно и больше они сюда не сунутся. Нужно доложить обо всем принцу.', 'Надеюсь, с них этого было довольно и больше они сюда не сунутся. Нужно доложить обо всем принцу.', 21154),
(92393, 'ruRU', '%s от души смеется.', '', 21154),
(92394, 'ruRU', 'Да, отличная идея. Возможно, несколько похожих предметов смогут освежить мою слабеющую память.', '', 21154),
(92395, 'ruRU', '', 'Enough of this! The Tidestone will be ours!', 21154),
(92403, 'ruRU', 'Ха-ха-ха! Имя нам – Легион. Мы знаем тебя, Аллари, – и тебя, $p. Ваши души станут нашими.', '', 21154),
(92412, 'ruRU', '', '', 21154),
(92413, 'ruRU', '', '', 21154),
(92414, 'ruRU', '', '', 21154),
(92415, 'ruRU', '', 'А ну выкладывай все, что тебе известно, гнусное создание!', 21154),
(92416, 'ruRU', '', 'Скорее, |3-6($c), – мы должны заставить души демонов заговорить.', 21154),
(92417, 'ruRU', 'Фрумпни меня в дукер, Ука. Я забираю жратву, и ничего ты мне не сделаешь, шнуропс!', 'Фрумпни меня в дукер, Ука. Я забираю жратву, и ничего ты мне не сделаешь, шнуропс!', 21154),
(92418, 'ruRU', '', 'Что говорить дукер горкнутый?', 21154),
(92421, 'ruRU', 'Приближается $gнезнакомец:незнакомка;!', '', 21154),
(92442, 'ruRU', 'Это дворец принца. Давай-ка прикончим этих наг, прежде чем возвращаться.', 'Это дворец принца. Давай-ка прикончим этих наг, прежде чем возвращаться.', 21154),
(92443, 'ruRU', 'Stay close, $n. Do not leave my side.', '', 21154),
(92444, 'ruRU', '$p? Where are you going?', '', 21154),
(92445, 'ruRU', 'Please stay close, $p.', '', 21154),
(92446, 'ruRU', 'I cannot do this on my own. Stay with me.', '', 21154),
(92447, 'ruRU', 'Where are you?', '', 21154),
(92448, 'ruRU', 'I am truly sorry.', '', 21154),
(92540, 'ruRU', 'Отвечай, где Приливный камень?', '', 21154),
(92541, 'ruRU', 'Отдай нам приливный камень!', '', 21154),
(92545, 'ruRU', '', 'Мы – первая и единственная линия обороны на пути Пылающего Легиона.', 21154),
(92547, 'ruRU', 'Мне нужно отремонтировать мое снаряжение.', 'Мне нужно отремонтировать мое снаряжение.', 21154),
(92550, 'ruRU', 'Я должен сражаться на передовой. В эту самую минуту мои руки должны были душить очередного демона.', '', 21154),
(92551, 'ruRU', 'Свяжи меня. Возможно, какое-то время я пробуду здесь.', 'Свяжи меня. Возможно, какое-то время я пробуду здесь.', 21154),
(92552, 'ruRU', '', '$p, расколи первый самоцвет души и выпусти заточенную в нем душу демона.', 21154),
(92556, 'ruRU', 'Убирайся отсюда. И чтобы я тебя здесь больше не видел!', '', 21154),
(92557, 'ruRU', 'Давай сюда бойцовскую жратву, иначе тебе несдобровать!', 'Давай сюда бойцовскую жратву, иначе тебе несдобровать!', 21154),
(92592, 'ruRU', 'Эти яйца помогут добиться идеального баланса между текстурой коктейля и его чародейской силой.', '', 21154),
(92593, 'ruRU', 'Мой шедевр войдет в историю.', '', 21154),
(92594, 'ruRU', 'Ну и теперь последние штрихи.', '', 21154),
(92595, 'ruRU', 'Твои учителя должны быть у начала подъема к западу отсюда. И поторопись, чтобы успеть до второго звонка!', '', 21154),
(92596, 'ruRU', 'И пьем до дна!', '', 21154),
(92597, 'ruRU', 'Ой-ой!', '', 21154),
(92598, 'ruRU', '', 'Гаруход? Он взорвался!', 21154),
(92599, 'ruRU', '', 'Начнем с самой простой чародейской руны. Воспроизведите ее очертания на земле своими шагами. Запомните ее. Зафиксируйте в памяти все тонкости.', 21154),
(92600, 'ruRU', '', 'Отлично. Теперь перейдем к огненной руне. Только осторожно: того, кто начертает эту руну неправильно, ждет скорая, но очень болезненная смерть.', 21154),
(92601, 'ruRU', '', 'Отлично! Ну и напоследок руна льда. Только не забывайте про пятый круг! Все постоянно забывают начертать пятый круг.', 21154),
(92602, 'ruRU', '', 'Все, на сегодня достаточно.', 21154),
(92604, 'ruRU', '', 'Готовься... Огонь!', 21154),
(92605, 'ruRU', '', 'Тщательно прицелься – и стреляй!', 21154),
(92606, 'ruRU', '', 'Наведи жезл на цель – и стреляй!', 21154),
(92607, 'ruRU', '', 'Огонь!', 21154),
(92608, 'ruRU', '', 'Стреляй!', 21154),
(92609, 'ruRU', '', 'Огонь по готовности!', 21154),
(92610, 'ruRU', '', 'Можешь начинать стрелять в любой момент.', 21154),
(92611, 'ruRU', '', 'Готовься! Целься! Стреляй!', 21154),
(92612, 'ruRU', '', 'Отлично!', 21154),
(92613, 'ruRU', '', 'Прекрасное попадание!', 21154),
(92614, 'ruRU', '', 'Потрясающе!', 21154),
(92615, 'ruRU', '', 'Невероятно!', 21154),
(92616, 'ruRU', '', 'Я вижу, мои уроки не прошли для тебя даром.', 21154),
(92617, 'ruRU', '', 'Безупречный выстрел – мои поздравления.', 21154),
(92618, 'ruRU', '', 'Превосходно.', 21154),
(92619, 'ruRU', '', 'Очень хорошо.', 21154),
(92620, 'ruRU', '', 'Нет, нет, все не так! У тебя совершенно сбился прицел!', 21154),
(92621, 'ruRU', '', 'Ну же, сосредоточься!', 21154),
(92622, 'ruRU', '', 'Ох... соберись!', 21154),
(92623, 'ruRU', '', '<Вздох...>', 21154),
(92624, 'ruRU', '', 'Нет, так не пойдет. Это никуда не годится.', 21154),
(92625, 'ruRU', '', 'Нет. Ну ничего, попробуем еще раз.', 21154),
(92626, 'ruRU', '', 'Тебе нужно поработать над прицеливанием.', 21154),
(92627, 'ruRU', '', 'Нет, это неудовлетворительно.', 21154),
(92628, 'ruRU', '', 'Твоя очередь.', 21154),
(92629, 'ruRU', '', 'Теперь твой черед.', 21154),
(92630, 'ruRU', '', 'А теперь ты попробуй.', 21154),
(92631, 'ruRU', '', 'Следующий студент!', 21154),
(92632, 'ruRU', 'За Гул''дана! За Легион!', '', 21154),
(92633, 'ruRU', 'За... Гул''дана...', '', 21154),
(92634, 'ruRU', '$b', '', 21154),
(92635, 'ruRU', 'Хр-р-р... о нет, госпожа директриса! Только не бейте!.. Хр-р-р...', 'Хр-р-р... о нет, госпожа директриса! Только не бейте!.. Хр-р-р...', 21154),
(92636, 'ruRU', 'Пожалуйста, еще пять минуточек...', 'Пожалуйста, еще пять минуточек...', 21154),
(92637, 'ruRU', 'Отречение, Сотворение, Предсказание, Некромантия...', 'Отречение, Сотворение, Предсказание, Некромантия...', 21154),
(92638, 'ruRU', '<вздыхает>... Я над этим свитком уже, кажется, десять тысяч лет просидел – и все равно так ничего и не понял...', '<вздыхает>... Я над этим свитком уже, кажется, десять тысяч лет просидела – и все равно так ничего и не поняла...', 21154),
(92639, 'ruRU', 'Надеюсь, здесь не было никаких происшествий.', 'Надеюсь, здесь не было никаких происшествий.', 21154),
(92647, 'ruRU', 'Обыщите здесь все! Пусть от этих руин камня на камне не останется!', '', 21154),
(92648, 'ruRU', 'Приливный камень будет нашим!', '', 21154),
(92662, 'ruRU', '', '', 21154),
(92663, 'ruRU', '', '', 21154),
(92664, 'ruRU', 'Убить незваного гостя!', 'Убить незваного гостя!', 21154),
(92665, 'ruRU', 'Мы – Железная Орда! Нас не победить!', 'Мы – Железная Орда! Нас не победить!', 21154),
(92666, 'ruRU', 'Убейте всех! В живых никого не оставлять!', 'Убейте всех! В живых никого не оставлять!', 21154),
(92667, 'ruRU', 'Лок''тар огар!', 'Лок''тар огар!', 21154),
(92668, 'ruRU', 'Твоя голова станет отличным трофеем!', 'Твоя голова станет отличным трофеем!', 21154),
(92669, 'ruRU', 'У нас железная воля!', 'У нас железная воля!', 21154),
(92687, 'ruRU', 'You are a fool for coming here, mortals. All of your accomplishments... all the foes you''ve struck down... all mean nothing here.', '', 21154),
(92688, 'ruRU', 'Feast, my minions. Bring me the Ashbringer.', '', 21154),
(92689, 'ruRU', 'Your schemes are through, demon. We''ve come to destroy you once and for all.', '', 21154),
(92690, 'ruRU', 'I''ve had enough of your meddling. This ends now.', '', 21154),
(92691, 'ruRU', 'We stand together, champion.', '', 21154),
(92694, 'ruRU', '<Огромный дракон дышит тяжело и прерывисто. Его терзает сильная боль.>', '', 21154),
(92696, 'ruRU', 'Hero... the Ashbringer... it is the only way... take it!', '', 21154),
(92697, 'ruRU', 'Ты смеешь вторгаться в мой порт и убивать моих воинов? Ну так сразись со мной и узнай, что такое Железная Орда!', '', 21154),
(92702, 'ruRU', 'Рубаки, у вас что, совсем нет гордости? Тренируйтесь как следует, не то отправитесь на корм рыбам!', '', 21154),
(92703, 'ruRU', 'Гул''дан не потерпит слабости!', '', 21154),
(92704, 'ruRU', 'Имейте в виду – будете и дальше халтурить, благословения Гул''дана вам не видать, как своих ушей!', '', 21154),
(92705, 'ruRU', 'Вы совсем не стараетесь. Будете получать половинную пайку, пока не соберетесь!', '', 21154),
(92708, 'ruRU', 'Это тебя тот полудохлый ящер и его племя послали? Больше никто не подчинит меня своей воле!', '', 21154),
(92709, 'ruRU', 'И вот так, по-вашему, дерутся трезубцем?', '', 21154),
(92710, 'ruRU', 'Учитесь драться, жалкие водяные черви!', '', 21154),
(92711, 'ruRU', 'Выше трезубец, ленивые змеи!', '', 21154),
(92721, 'ruRU', '', 'You have some nerve showing your face around here.', 21154),
(92726, 'ruRU', '', 'За королеву!', 21154),
(92727, 'ruRU', '', 'Мы заберем Приливный камень, и нас никто не сможет остановить!', 21154),
(92728, 'ruRU', '', 'Гнев Азшары поглотит тебя!', 21154),
(92729, 'ruRU', '', 'Хочешь погрузиться в пучину?', 21154),
(92730, 'ruRU', 'For Nazjatar!', '', 21154),
(92731, 'ruRU', 'Your bones will sharpen my trident.', '', 21154),
(92732, 'ruRU', 'You dare stand against us?', '', 21154),
(92739, 'ruRU', 'Я возглавлю атаку на западные ворота и отвлеку силы Железной Орды на себя. Это даст вам с Дрекой достаточно времени, чтобы организовать операционную базу у них на фланге.', '', 21154),
(92740, 'ruRU', 'Пора начинать вторжение.', 'Пора начинать вторжение.', 21154),
(92742, 'ruRU', 'The prisoners are breaking free!', '', 21154),
(92743, 'ruRU', 'It''s an uprising!', '', 21154),
(92744, 'ruRU', 'Так, парни, пристань сама себя не расчистит! За работу!', '', 21154),
(92769, 'ruRU', 'Свет хранит нас всех, друг.', '', 21154),
(92770, 'ruRU', 'После тебя, $n. Твое присутствие здесь крайне важно.', '', 21154),
(92771, 'ruRU', 'Покойся с миром, брат... мы никогда не забудем битву на Расколотом берегу.', '', 21154),
(92772, 'ruRU', 'Эти синие чешуйчатые твари, которыми командовал Малигос, убили моих друзей и всю мою семью.$b$bМожешь делать со мной все, что хочешь, но знай, что я буду охотиться за ними до тех пор, пока все они не прочувствуют мою боль.', '', 21154),
(92773, 'ruRU', 'Я... Я тебя помню. По Нордсколу. Ты тогда $gпомогал:помогала; мне допрашивать прислужников этих синих чешуйчатых тварей.$b$bПо приказу Малигоса они убили моих друзей и всю мою семью – только потому, что им не нравилось, как мы использовали магию.$b$bМожешь делать со мной все, что хочешь, но знай, что я буду охотиться за ними до тех пор, пока все они не прочувствуют мою боль.', '', 21154),
(92774, 'ruRU', 'Оставь ему жизнь.', 'Оставь ему жизнь.', 21154),
(92775, 'ruRU', 'Прикончи его.', 'Прикончи его.', 21154),
(92777, 'ruRU', 'Нет! Дай мне прикончить этих тварей! Я должен отомстить!', '', 21154),
(92778, 'ruRU', 'Я заставлю вас заплатить за все, что вы сделали в Нордсколе!', '', 21154),
(92779, 'ruRU', 'Вы убили в Нордсколе всех моих друзей, синие чешуйчатые твари!', '', 21154),
(92781, 'ruRU', 'Сострадание? Я уж и забыл, что это такое. Я запомню это, |3-6($r).', '', 21154),
(92784, 'ruRU', '', 'Тирион сказал, что ты придешь. Час пробил?', 21154),
(92785, 'ruRU', 'Да, пробил наш час. Мы выступаем немедленно.', 'Да, пробил наш час. Мы выступаем немедленно.', 21154),
(92786, 'ruRU', '', 'Я с тобой, $n. ((Лиадрин выбегает наружу))', 21154),
(92791, 'ruRU', 'Подойди ближе, дитя. Я не так плох, как кажется.', '', 21154),
(92792, 'ruRU', 'Прошу простить моих внучек. Им кажется, что они должны защищать меня от $gкаждого:каждой:r; |3-1($r) – |3-1($c), кому случится здесь проходить.$b$bМожешь продолжать.', '', 21154),
(92793, 'ruRU', '<Положить руку на рог Сенегоса.>', '<Положить руку на рог Сенегоса.>', 21154),
(92794, 'ruRU', '', 'Отдохни, дедушка! Тебе нужно беречь энергию.', 21154),
(92795, 'ruRU', 'Не торопись скорбеть, звезда моя. Меня ждет последнее приключение.', '', 21154),
(92796, 'ruRU', '<При вашем приближении дракончик с любопытством смотрит на вас и улыбается.>', '', 21154),
(92797, 'ruRU', 'Ты отнесешь меня в Покой Лазурного Крыла?', 'Ты отнесешь меня в Покой Лазурного Крыла?', 21154),
(92798, 'ruRU', '<Перед вами – не сам Сенегос, а его магическая проекция. Он выглядит сильным, жизнерадостным и уверенным в себе. Ничего общего с тем угасающим драконом, которого вы обнаружили в пруду.>', '', 21154),
(92822, 'ruRU', '[PH] I NEED TEXT', '[PH] I NEED TEXT', 21154),
(92823, 'ruRU', 'Я готов и жду только твоего приказания, $n.', '', 21154),
(92824, 'ruRU', 'Тогда поспешим.', 'Тогда поспешим.', 21154),
(92825, 'ruRU', 'Очень хорошо. Увидимся у Верховного лорда. ((Гриан выбегает наружу))', '', 21154),
(92826, 'ruRU', 'Нет... НЕТ! Я больше не потерплю неудач!', '', 21154),
(92829, 'ruRU', 'Я буду снаружи – подготовлю пока наших лошадей. Я основал орден Серебряной Длани, чтобы уничтожить Пылающий Легион, и это будет его первая битва. ((Тирион выходит))', '', 21154),
(92835, 'ruRU', 'На вас наложено |TInterface\\Icons\\Spell_Shadow_ChillTouch.blp:20|t  |cFFFF0404|Hspell:179977|h["Касание рока"]|h|r!', 'На вас наложено |TInterface\\Icons\\Spell_Shadow_ChillTouch.blp:20|t  |cFFFF0404|Hspell:179977|h["Касание рока"]|h|r!', 21154),
(92839, 'ruRU', '', 'Мародеры Кровавой Глазницы. Если их не перебить, они станут угрозой снабжению.', 21154),
(92844, 'ruRU', '', 'Мы готовы и ждем твоего приказа, Верховный лорд.', 21154),
(92845, 'ruRU', '', 'Головорезы Гул''дана. Не знаю, что они затеяли рядом с Темным Порталом, но они стоят у нас на пути.', 21154),
(92849, 'ruRU', 'Мы выступим по твоей команде, друг.', '', 21154),
(92851, 'ruRU', 'Ха! Эти пушки просто бесполезны! Зацени-ка вот наше последнее творение – орка Скверны!', '', 21154),
(92852, 'ruRU', 'Легион наверняка полагает, что теперь-то уж с нами покончено. Но мы покажем им, как они заблуждаются.', '', 21154),
(92853, 'ruRU', '', 'После тебя, $n. Твое присутствие здесь крайне важно.', 21154),
(92854, 'ruRU', 'Entering', 'Entering', 21154),
(92855, 'ruRU', 'А, $gмой друг:моя подруга;. $gДобрый надежный друг:Добрая надежная подруга; $n.$b$bУ тебя случайно не найдется ли при себе лишних кристаллов маны?$b$bПослушай. Если тебе вдруг что-нибудь понадобится – все что угодно, ты не стесняйся обращаться к своему верному другу Рунасу. Я мигом решу все твои проблемы. Ты всегда можешь рассчитывать на меня.$b$bНу, разумеется, если что-то понадобится мне, ты тоже поможешь, да? Наприме', '', 21154),
(92856, 'ruRU', 'Просто показывай дорогу, Рунас.', '', 21154),
(92861, 'ruRU', '', 'Командир, скорее сюда!', 21154),
(92862, 'ruRU', '', 'Вот просто отличное место. Все вокруг просматривается на многие мили. Ставь свое знамя, командир.', 21154),
(92863, 'ruRU', '', 'Мы уже идем!', 21154),
(92866, 'ruRU', 'Доступно дополнительное задание!\n|TINTERFACE\\GLUES\\CREDITS\\CrystalSong1TGA1.blp:64|t|TINTERFACE\\GLUES\\CREDITS\\CrystalSong1TGA2.blp:64|t|TINTERFACE\\GLUES\\CREDITS\\CrystalSong1TGA3.blp:64|t\n|TINTERFACE\\GLUES\\CREDITS\\CrystalSong1TGA5.blp:64|t|TINTERFACE\\GLUES\\CREDITS\\CrystalSong1TGA6.blp:64|t|TINTERFACE\\GLUES\\CREDITS\\CrystalSong1TGA7.blp:64|t\n|cFFFFFFFFСиловые руины Заркенара|r', '', 21154),
(92867, 'ruRU', 'Эй, ты что это делаешь?', '', 21154),
(92868, 'ruRU', 'Так ты меня не знаешь?', '', 21154),
(92869, 'ruRU', 'Я сдаюсь! Прошу, прости меня! Я очень-очень раскаиваюсь.', '', 21154),
(92870, 'ruRU', 'Такая грубая энергия... затуманивает сознание.', '', 21154),
(92871, 'ruRU', 'Надеюсь, тебя не смутят мой потрепанный вид и вольность поведения. Я вовсе не монстр.', '', 21154),
(92872, 'ruRU', 'Я тебе докажу. Я выпущу на волю всех этих малышей-дракончиков, которых собирался убить.', '', 21154),
(92873, 'ruRU', 'Видишь? Я на твоей стороне.', '', 21154),
(92874, 'ruRU', 'Ладно. Я тебе докажу, что я не какой-нибудь маноед вроде этих.', '', 21154),
(92875, 'ruRU', 'Ой, это ж кристалл маны! Дай хоть вспомню вкус.', '', 21154),
(92876, 'ruRU', 'Мы с тобой – друзья навек. Мы с тобой горы свернем.', '', 21154),
(92877, 'ruRU', 'Знатное оружие, дружище!', '', 21154),
(92878, 'ruRU', 'Куда мы идем? Ты водишь дружбу с драконами? Как любопытно...', '', 21154),
(92879, 'ruRU', 'Кто это?', '', 21154),
(92880, 'ruRU', 'Я – Рунас, сын Индаррила, родом из Сурамара. Очень приятно.', '', 21154),
(92881, 'ruRU', 'Помраченный. Я не доверяю тебе подобным.', '', 21154),
(92882, 'ruRU', 'Понимаю, друг-дракон. О моих сородичах идет дурная слава, но я все исправлю!', '', 21154),
(92883, 'ruRU', 'Чудесно! Ну, тогда я в Заркенар. Повидаюсь с друзьями, родными и прочими близкими.', '', 21154),
(92884, 'ruRU', '...ну, чтобы убить их всех. Отомстить за твоего дракона.', '', 21154),
(92885, 'ruRU', 'Горстка жалких любителей маны. Хорошо, что я не такой и предпочитаю железо.', '', 21154),
(92886, 'ruRU', 'Ой, кристаллик маны. Минутку!', '', 21154),
(92887, 'ruRU', 'Так... ты с какой целью в Азсуне, дружище? Просто так или...', '', 21154),
(92888, 'ruRU', 'Ты когда-нибудь молчишь?', '', 21154),
(92889, 'ruRU', 'Прости, друг-дракон. А вот мы и на месте!', '', 21154),
(92890, 'ruRU', 'Мы на месте! Дом, милый дом...', '', 21154),
(92905, 'ruRU', '|TInterface\\Icons\\inv_blacksmithdye_black.blp:20|t %s роняет на землю |cFFFF0000|Hspell:180147|h[боеприпасы огня Скверны]|h|r.', '|TInterface\\Icons\\inv_blacksmithdye_black.blp:20|t %s роняет на землю |cFFFF0000|Hspell:180147|h[боеприпасы огня Скверны]|h|r.', 21154),
(92912, 'ruRU', '', 'Пылающий Легион бросил против нас своих самых могущественных демонов. Они определенно не хотят проигрывать третью войну подряд и не остановятся ни перед чем, чтобы окончательно стереть нас всех с лица Азерота.\n\nНо у нас на этот счет свои планы.', 21154),
(92922, 'ruRU', '', 'Сейчас отдышусь...', 21154),
(92931, 'ruRU', '', 'Странно, что Орда Гул''дана покинула это место. Я ожидала сильного сопротивления.', 21154),
(92932, 'ruRU', '', 'Воины Кровавой Глазницы бродят по округе. Где их командование?', 21154),
(92933, 'ruRU', '', 'Что замышлял этот чернокнижник? Я лучше выйду против пушек Железной Орды.', 21154),
(92934, 'ruRU', '', 'Насчет пушек я погорячилась. Придется прорываться с боем.', 21154),
(92936, 'ruRU', 'Drawing Power', 'Drawing Power', 21154),
(92937, 'ruRU', 'Они... делали со мной... страшные вещи...', '', 21154),
(92938, 'ruRU', 'Я не сдался.', '', 21154),
(92939, 'ruRU', 'Невероятную силу.', '', 21154),
(92940, 'ruRU', 'Нас пыталась подтолкнуть к предательству Стражница. Я слышал крики.', '', 21154),
(92941, 'ruRU', 'Циана Ночная Глефа предала нас. Она присоединилась к Легиону.', '', 21154),
(92942, 'ruRU', 'Ночная Глефа ушла на юго-запад вместе со Стражницей по имени Кордана Оскверненная Песнь.', '', 21154),
(92943, 'ruRU', 'Мое пламя поглотит всех!', '', 21154),
(92944, 'ruRU', '|TInterface\\Icons\\ability_fixated_state_purple:20|t%s  |cFFFF0404|Hspell:180148|h[сосредотачивает внимание]|h|r на вас!', '|TInterface\\Icons\\ability_fixated_state_purple:20|t%s |cFFFF0404|Hspell:180148|h[сосредотачивает внимание]|h|r на вас!', 21154),
(92950, 'ruRU', 'Доставь меня на Железный фронт.', 'Доставь меня на Железный фронт.', 21154),
(92969, 'ruRU', 'Эти новые орки Скверны сражаются, как бешеные. Все это еще не скоро закончится...', '', 21154),
(92970, 'ruRU', 'Как только вы погрузились на корабли, мы сразу начали осаду. Поначалу действовали осторожно, но неожиданно большой отряд Железной Орды отошел – вероятно, дать тебе бой. Вот тут-то мы на них и навалились!$b$bТвой дружок Газлоу смастерил какую-то адскую штуковину, чтобы сломать ворота. Мы ринулись внутрь... и столкнулись с новым могучим противником – орками Скверны. Они остановили наше продвижение, так что пока ', '', 21154),
(92971, 'ruRU', ';p;', '', 21154),
(92972, 'ruRU', 'Доложи обстановку.', 'Доложи обстановку.', 21154),
(92973, 'ruRU', 'Еда', 'Еда', 21154),
(92974, 'ruRU', 'Каз велела мне тебе помочь! Ух ты, как тут здорово!$b$bВ общем, я тебе могу рассказать про Зет''Гол – это на северо-западе; про храм Ша''наар на юге и про Зорамтопь на юго-востоке.', '', 21154),
(92975, 'ruRU', 'Хм... Так у тебя есть для меня важные сведения, разведчик Порез?', 'Хм... Так у тебя есть для меня важные сведения, разведчик Порез?', 21154),
(92976, 'ruRU', 'Да! Короче, с этими ребятами из клана Кровавой Глазницы лучше не шутить. Они так же опасны, как Веселый Череп, только любят подолгу пялиться в небо. Постоянно охотятся в джунглях с капканами и ловушками. А в своем городе чего-то странное творят с кровью и темной магией.$b$bВот бы выпустить на них магнарона, чтобы он там все разнес в щепки!', '', 21154),
(92977, 'ruRU', 'Командир, будь начеку! Саргераи уже здесь!', '', 21154),
(92978, 'ruRU', 'Саргераи? А что здесь происходит?', 'Саргераи? А что здесь происходит?', 21154),
(92979, 'ruRU', 'Недавно они тут вырезали всех местных. Я не смог подобраться достаточно близко, но точно могу сказать, что они заражают диких зверей Скверной и призывают демонов.$b$bЭту проклятую деревню нужно сжечь дотла, командир.', '', 21154),
(92980, 'ruRU', '', 'Стеллагоса, нет... вернись! Проклятье!', 21154),
(92981, 'ruRU', 'Здорово, командир. В лагере Актара объявились араккоа. И еще я заглянул в здоровенный туннель на юго-востоке.', '', 21154),
(92982, 'ruRU', 'А что тут делают араккоа?.. И кто это?', 'А что тут делают араккоа?.. И кто это?', 21154),
(92983, 'ruRU', 'Это и есть Актар. Последователи Сете и Совет Теней тусят тут в руинах поблизости – а он, значит, за ними присматривает. И еще нам тут досаждают крылатые араккоа. Короче, птичьего помету будет – не разгрести.$b$bДа, и еще – ежели тебе надо будет двинуть на восток, тут есть туннель на юго-востоке. Выведет тебя прямиком к гавани Железной крепости. Только там, наверное, опасно.', '', 21154),
(92984, 'ruRU', 'А теперь покажите мне, чему вы научились!', 'А теперь покажите мне, чему вы научились!', 21154),
