(95341, 'ruRU', 'УТОПИТЬ!', '', 21154),
(95342, 'ruRU', 'СПАЛИТЬ ДОТЛА!', '', 21154),
(95343, 'ruRU', 'ЗЕМЛЯ РАЗДАВИТ ВАС!', '', 21154),
(95344, 'ruRU', 'НЕ УБЕЖИТЕ!', '', 21154),
(95345, 'ruRU', 'УНИЧТОЖУ!', '', 21154),
