(105167, 'ruRU', '[You are currently eligible for Chapter 3.]', '', 21154),
(105168, 'ruRU', '', 'I see that you are no ally to these naga, $r! Get me out of this cage!', 21154),
(105169, 'ruRU', '', 'I must find Jarl Throndyr!', 21154),
