(108996, 'ruRU', 'You can''t counter what you can''t see!', '', 21154),
(108997, 'ruRU', 'I will speed you towards your demise!', '', 21154),
(108998, 'ruRU', 'If you blink... you bleed!', '', 21154),
(108999, 'ruRU', 'Another victim of my artistry.', '', 21154),
(109000, 'ruRU', 'Never heard by blade coming!', '', 21154),
(109001, 'ruRU', '', 'But I have heard the prayers of their faithful... crying out for destruction!', 21154),
(109002, 'ruRU', 'Elisande... forgive me!', '', 21154),
