(109353, 'ruRU', 'Time to crush your dreams!', '', 21154),
(109354, 'ruRU', 'Succumb to the madness!', '', 21154),
(109355, 'ruRU', 'Give in to the Nightmare!', '', 21154),
(109356, 'ruRU', 'Your dreams are mine!', '', 21154),
