(110882, 'ruRU', 'Whirlwind can damage multiple enemies!', '', 21154),
(110883, 'ruRU', 'Use Victory Rush!', '', 21154),
(110884, 'ruRU', 'Victory Rush is available! Use it now!', '', 21154),
(110885, 'ruRU', 'Use Slam!', '', 21154),
(110886, 'ruRU', 'Use Mortal Strike!', '', 21154),
(110887, 'ruRU', 'Mortal Strike is available!  Use it now!', '', 21154),
(110888, 'ruRU', 'Use Colossus Smash!', '', 21154),
