(109783, 'ruRU', 'Treat the patient''s fever.', 'Treat the patient''s fever.', 21154),
(109784, 'ruRU', 'Too simple. Honor Guard: Finish them.', '', 21154),
(109785, 'ruRU', 'Are you whole? We had better hurry before they discover we were not properly dispatched.', '', 21154),
(109786, 'ruRU', 'Azsuna will fall before the Legion!', '', 21154),
(109787, 'ruRU', 'We cannot... be stopped...', '', 21154),
